def find_floor(input):
    curr_floor = 0

    for instruction in input:
        if instruction == '(':
            curr_floor += 1
        elif instruction == ')':
            curr_floor -= 1

    return curr_floor


def find_first_time_to_basement(input):
    curr_floor = 0

    for i in xrange(len(input)):
        if input[i] == '(':
            curr_floor += 1
        elif input[i] == ')':
            curr_floor -= 1

        if curr_floor == -1:
            return i + 1


